import React, { Component } from 'react';
import { Card, CardImg, CardImgOverlay, CardText, CardBody, CardTitle } from 'reactstrap';
import Dishdetail from './DishdetailComponents.js';

class Menu extends Component {

    constructor(props) {
        // super is required
        super(props);

        this.state={
            selectedDish: null
            // state stores properties
            // related to the component
            // that we can use
        }
        console.log('Menu Component constructor is invoked')
    }

    componentDidMount() {
        console.log('Menu Component componentDidMount is invoked')

    }

    onDishSelect(dish) {
        this.setState({selectedDish: dish});
    }


    render() {
        // map dishes array onto a list of items
        const menu = this.props.dishes.map((dish) => {
            //iteratie over every dish in the dishes array
            //arrow indictes whats going to be returned by the map operator

            return (
                //for every dish, return: the layout for the dish
                //key: when constructing a list of items in React, every item requries a "key" attribute
                //key helps react recognize each one of these elements

                //inside the div:
                //the media tag "li": each dish is a list item
                <div key={dish.id} className="col-12 col-md-1 m-1">
                    <Card onClick={() => this.onDishSelect(dish) }>
                        <CardImg width="100%" src={dish.image} alt={dish.name} />
                        <CardImgOverlay>
                            <CardTitle>{dish.name}</CardTitle>
                        </CardImgOverlay>
                    </Card>
             </div>
            );
        });

        console.log('Menu Component render is invoked')

        return(
            <div className="container">
                <div className="row">
                    {menu}
                </div>
                <div className="row">
                    {this.state.selectedDish != null && <Dishdetail dish = {this.state.selectedDish}/>}          
                </div>
            </div>
        );
    };
}

export default Menu;
