import React, { Component } from 'react';
import Home from './HomeComponent';
import Menu from './MenuComponents';
import About from './AboutComponent';
import Contact from './ContactComponent';
import Header from './HeaderComponent';
import Footer from './FooterComponent';
import { DISHES } from '../share/dishes';
import { COMMENTS } from '../share/comments';
import { PROMOTIONS } from '../share/promotions';
import { LEADERS } from '../share/leaders';
import Dishdetail from './DishdetailComponents.js';
import { Switch, Route, Redirect } from 'react-router-dom';


class Main extends Component {
  constructor(props) {
    super(props);
    this.state= {
      dishes: DISHES,
      comments: COMMENTS,
      promotions: PROMOTIONS,
      leaders: LEADERS
    };
  }

  onDishSelect(dishID) {
    this.setState({selectedDish: dishID});
  }

  render() {

    const HomePage = () => {
        return(
          <Home 
            dish={this.state.dishes.filter((dish) => dish.featured === true)[0]}
            promo={this.state.promotions.filter((promo) => promo.featured === true)[0]}
            leader={this.state.leaders.filter((leader) => leader.featured === true)[0]}
          />
        );
    }

    const DishWithId = ({match}) => {
        return(
            <Dishdetail dish={this.state.dishes.filter((dish) => dish.id === parseInt(match.params.dishId,10))[0]}
                comments={this.state.comments.filter((comment) => comment.dishId === parseInt(match.params.dishId,10))}
                />
        )
    }

    return (
      <div> 
        <Header />
        <Switch>
            <Route path ="/home" component={HomePage} />
            <Route path ="/aboutus" component = { () => <About leaders ={this.state.leaders} />} />
            <Route exact path ="/menu" component={ () => <Menu dishes ={this.state.dishes} />} />
            <Route path="/menu/:dishId" component={DishWithId} />
            <Route exact path="/contactus" component={Contact} />
            <Redirect to="/home" />
        </Switch>
        <Footer />
      </div>
    );
  }
}

export default Main;
